import { createElectricTransition } from '../index';
describe('animation', () => {
    describe('createElectricTransition()', () => {
        test('returns undefined if no property is entered', () => {
            const result = createElectricTransition();
            expect(result).toBe(undefined);
        });
        test('returns correct transition for single property', () => {
            const result = createElectricTransition('opacity');
            expect(result).toBe('transition: opacity 0.3s cubic-bezier(0.14, 1.12, 0.67, 0.99)');
        });
        test('returns correct transition for single value with delay', () => {
            const result = createElectricTransition({
                name: 'opacity',
                delay: '0.5s'
            });
            expect(result).toBe('transition: opacity 0.3s cubic-bezier(0.14, 1.12, 0.67, 0.99) 0.5s');
        });
        test('returns correct transition for single value with custom duration', () => {
            const result = createElectricTransition({
                name: 'opacity',
                duration: '0.6s'
            });
            expect(result).toBe('transition: opacity 0.6s cubic-bezier(0.14, 1.12, 0.67, 0.99)');
        });
        test('returns correct transition for multiple values', () => {
            const result = createElectricTransition('opacity', {
                name: 'height',
                duration: '0.8s',
                delay: '0.6s'
            });
            expect(result).toBe('transition: opacity 0.3s cubic-bezier(0.14, 1.12, 0.67, 0.99), height 0.8s cubic-bezier(0.14, 1.12, 0.67, 0.99) 0.6s');
        });
    });
});
