import { createFullLayoutConfiguration, createLayoutConfigurationLines } from '../layout';
describe('Layout', () => {
    describe('createFullLayoutConfiguration', () => {
        test('for 2 areas with a 2 area configuration', () => {
            const config = [{ name: 'a', columns: 1 }, { name: 'b', columns: 2 }];
            const expectation = [{ columns: 1, name: 'a0' }, { columns: 2, name: 'b0' }];
            expect(createFullLayoutConfiguration(config, 2)).toEqual(expectation);
        });
        test('for 3 areas with a 2 area configuration', () => {
            const config = [{ name: 'a', columns: 1 }, { name: 'b', columns: 2 }];
            const expectation = [
                { columns: 1, name: 'a0' },
                { columns: 2, name: 'b0' },
                { columns: 1, name: 'a1' },
                { columns: 2, name: 'b1' }
            ];
            expect(createFullLayoutConfiguration(config, 3)).toEqual(expectation);
        });
        test('for 5 areas with a 3 area configuration', () => {
            const config = [
                { name: 'a', columns: 1 },
                { name: 'b', columns: 2 },
                { name: 'c', columns: 3 }
            ];
            const expectation = [
                { columns: 1, name: 'a0' },
                { columns: 2, name: 'b0' },
                { columns: 3, name: 'c0' },
                { columns: 1, name: 'a1' },
                { columns: 2, name: 'b1' },
                { columns: 3, name: 'c1' }
            ];
            expect(createFullLayoutConfiguration(config, 5)).toEqual(expectation);
        });
    });
    describe('createLayoutConfigurationLines', () => {
        test('for an area with up to 24 columns', () => {
            const config = [{ columns: 24, name: 'a' }];
            const expectation = [new Array(24).fill('a')];
            expect(createLayoutConfigurationLines(config)).toEqual(expectation);
        });
        test('for areas with more than 24 columns', () => {
            const config = [
                { columns: 12, name: 'a' },
                { columns: 12, name: 'b' },
                { columns: 12, name: 'c' }
            ];
            const expectation = [
                new Array(24).fill('a', 0, 12).fill('b', 12, 24),
                new Array(12).fill('c')
            ];
            expect(createLayoutConfigurationLines(config)).toEqual(expectation);
        });
    });
});
