import { TextAreaAppearance, TextInputAppearance } from './shared';
export const getColor = (props) => {
    let color;
    if (!props.theme) {
        return;
    }
    switch (props.appearance) {
        case TextAreaAppearance.ReadOnly:
        case TextInputAppearance.ReadOnly:
            color = props.theme.interaction.recipe100.default;
            break;
        case TextAreaAppearance.Disabled:
        case TextInputAppearance.Disabled:
            color = props.theme.interaction.recipe500.disabled;
            break;
        default:
            color = props.theme.interaction.recipe500.default;
    }
    return color;
};
