import { getDesignTokenValue } from '../get-design-token-value';
const exampleTokens = {
    color: {
        'green-100': '#ADE5A1'
    },
    size: {
        /* eslint-disable-next-line @typescript-eslint/camelcase */
        dynamic0020: [
            { breakpoint: '0', value: '0' },
            { breakpoint: '100px', value: '10%' },
            { breakpoint: '200px', value: '20%' },
            { breakpoint: '300px', value: '30%' }
        ]
    }
};
describe('getDesignTokenValue', () => {
    test('undefined', () => {
        expect(
        /* eslint-disable-next-line @typescript-eslint/no-explicit-any */
        getDesignTokenValue(undefined, {
            tokens: exampleTokens
        })).toBeFalsy();
    });
    test('unknown value', () => {
        expect(getDesignTokenValue('wurst', { tokens: exampleTokens })).toBeFalsy();
    });
    test('basic value', () => {
        expect(getDesignTokenValue('color.green-100', { tokens: exampleTokens })).toBe('#ADE5A1');
    });
    test('dynamic value', () => {
        expect(getDesignTokenValue('size.dynamic0020', { tokens: exampleTokens })).toBe('var(--size-dynamic0020)');
    });
});
