import 'jest-styled-components';
import { createTestStyledComponent } from '../../helpers/__tests__/helpers';
import { getMediaQueryVars } from '../get-media-query-vars';
const exampleTokens = {
    color: {
        'green-100': '#ADE5A1'
    },
    size: {
        /* eslint-disable-next-line @typescript-eslint/camelcase */
        dynamic0020: [
            { breakpoint: '0', value: '0' },
            { breakpoint: '100px', value: '10%' },
            { breakpoint: '200px', value: '20%' },
            { breakpoint: '300px', value: '30%' }
        ]
    }
};
describe('getMediaQueryVars', () => {
    test('returns the media query vars', () => {
        const css = getMediaQueryVars({ tokens: exampleTokens });
        const toTest = createTestStyledComponent(css);
        expect(toTest).toHaveStyleRule('--size-dynamic0020', '0');
        expect(toTest).toHaveStyleRule('--size-dynamic0020', '10%', {
            media: '(min-width: 100px)'
        });
        expect(toTest).toHaveStyleRule('--size-dynamic0020', '20%', {
            media: '(min-width: 200px)'
        });
        expect(toTest).toHaveStyleRule('--size-dynamic0020', '30%', {
            media: '(min-width: 300px)'
        });
    });
});
